default_stopwords_file = "/home/ubuntu/code_assist_python/src/stopwords/SmartStoplist.txt"
class KeywordsExtractor:
	def __init__(self, stopwords_file = default_stopwords_file):
		if not stopwords_file:
			stopwords_file = default_stopwords_file
		self.Rake = RAKE.Rake(stopwords_file) #, p2_keywords)

	def get_keywords(self, text):
		reserved_kws = re.findall('"([^"]*)"', text)
		text_str = text
		for x in reserved_kws:
			text_str = text_str.replace('"'+x+'"', "")

		kws = self.Rake.run(text_str)
		#TODO: value sorting?
		return [x[0] for x in kws] + reserved_kws
